ALTER TABLE %db_prefix%alerts CHANGE parent_alert parent_alert VARCHAR(80) NOT NULL;
ALTER TABLE %db_prefix%alerts ADD alert_format_name VARCHAR(80) NULL AFTER parent_alert;
